mapper.select:


	select emp_id, dept_name, position, name, entry_date, resignation_date
		from ( 
			select /*+INDEX_DESC(employee entry_index) */ 
					rownum rn, emp_id, dept_name, position, name, entry_date, resignation_date
				from employee e JOIN department d
				on e.dept_id = d.dept_id
				where 



		 rownum <= #{pageNum} * #{amount} and resignation_date is null
	)where rn > (#{pageNum} -1) * #{amount}
and resignation_date is null order by entry_date desc



	select emp_id, dept_name, position, name, entry_date, resignation_date
		from ( 
			select /*+INDEX_DESC(employee rsg_index) */ 
					rownum rn, emp_id, dept_name, position, name, entry_date, resignation_date
				from employee e JOIN department d
				on e.dept_id = d.dept_id
				where 



		 rownum <= #{pageNum} * #{amount} and resignation_date is not null
	)where rn > (#{pageNum} -1) * #{amount}
and resignation_date is not null order by resignation_date desc
